mapper.select:
select *
from user
where email = #{email} and emailConfirmation = 1;

mapper.insert:
insert into user (email, password, intro, nickname, name) values (#{email}, #{password}, #{intro}, #{nickname}, #{name})
SELECT LAST_INSERT_ID()
;

mapper.update:
update user
set password = #{password}, intro = #{intro}, nickname = #{nickname}, tier = #{tier}, followingCnt = #{followingCnt}, followerCnt = #{followerCnt},
profilePicSrc = #{profilePicSrc}, profilePicName = #{profilePicName}, emailConfirmation = #{emailConfirmation}, status = #{status}
where id = #{id};

update follow
set accepted = 1
where followerId = #{followerId} and followedId = #{followedId};

mapper.select:
select * from user
where email like #{email}
limit 20
offset #{start};

select * from user
where nickname like #{nickname}
limit 20
offset #{start};

mapper.update:
update user
set profilePicSrc = #{profilePicSrc}, profilePicName = #{profilePicName}
where email = #{email};

mapper.select:
select * from follow
where followedId = #{id} and accepted = 0
limit 20
offset #{start}
;
